SELECT date as fullDate ,DATE_FORMAT(date,'%m-%d') as dateAbscissa ,(select count(1) from busi_booking bb inner join busi_project bp on bb.projguid = bp.guid inner join busi_project bp2 on bp.parent_id = bp2.id and
bp2.id in
#{id}
where DATE(bb.bgndate) = date and (bb.closeyy is null or bb.closeyy ='转认购') ) count
from( SELECT date_sub(DATE(#{endDate}),INTERVAL numlist.id day)AS 'date' FROM ( SELECT * from (SELECT i AS id FROM num ORDER BY i asc) a where a.id <=7 ) AS numlist ) temp;

SELECT date as fullDate ,DATE_FORMAT(date,'%Y %m') as dateAbscissa ,(select count(1) from busi_booking bb inner join busi_project bp on bb.projguid = bp.guid inner join busi_project bp2 on bp.parent_id = bp2.id and
bp2.id in
#{id}
where DATE_FORMAT(bb.bgndate,'%Y-%m') = DATE_FORMAT(date,'%Y-%m') and (bb.closeyy is null or bb.closeyy ='转认购') ) count from( SELECT date_sub(DATE(#{endDate}),INTERVAL numlist.id MONTH)AS 'date' FROM ( SELECT * from (SELECT i AS id FROM num ORDER BY i asc) a where a.id <=7 ) AS numlist ) temp;

SELECT date as fullDate ,DATE_FORMAT(date,'%Y') as dateAbscissa ,(select count(1) from busi_booking bb inner join busi_project bp on bb.projguid = bp.guid inner join busi_project bp2 on bp.parent_id = bp2.id and
bp2.id in
#{id}
where DATE_FORMAT(bb.bgndate,'%Y') = DATE_FORMAT(date,'%Y') and (bb.closeyy is null or bb.closeyy ='转认购') ) count from( SELECT date_sub(DATE(#{endDate}),INTERVAL numlist.id YEAR)AS 'date' FROM ( SELECT * from (SELECT i AS id FROM num ORDER BY i asc) a where a.id <=7 ) AS numlist ) temp;

select ywy,count(1) count from busi_booking bb inner join busi_project bp on bb.projguid = bp.guid
inner join busi_project bp2 on bp.parent_id = bp2.id and

    bp2.id in

        #{id}


where bgndate <= #{dateEnd} and #{dateStart} <= bgndate and (closeyy is null or closeyy ='转认购') GROUP BY ywy ORDER BY count desc;

select ywy,count(1) count from busi_trade bb inner join busi_project bp on bb.projguid = bp.guid
inner join busi_project bp2 on bp.parent_id = bp2.id and

    bp2.id in

        #{id}


where  qsdate <= #{dateEnd} and #{dateStart} <= qsdate
GROUP BY ywy ORDER BY count desc;

select ywy,count(1) count from busi_contract bb inner join busi_project bp on bb.projguid = bp.guid
inner join busi_project bp2 on bp.parent_id = bp2.id and

    bp2.id in

        #{id}


where  qsdate <= #{dateEnd} and #{dateStart} <= qsdate
GROUP BY ywy ORDER BY count desc;
