mapper.select:
select t1.*, t2.name as parent_name from busi_middle_type t1 left join busi_middle_type t2 on t1.parent_id = t2.id where exists(select 1 from busi_user_project t0 where t0.project_id=t1.project_id and t0.user_id=#{userId})
and t1.id = #{id}
and t1.parent_id=#{parentId}
and t1.name like '%${name}%'
and t2.parent_name like '%${parentName}%'
limit #{offset},#{limit}

select count(*) from busi_middle_type t1 left join busi_middle_type t2 on t1.parent_id = t2.id where exists(select 1 from busi_user_project t0 where t0.project_id=t1.project_id and t0.user_id=#{userId})
and t1.id = #{id}
and t1.parent_id=#{parentId}
and t1.name like '%${name}%'
and t2.parent_name like '%${parentName}%'
