mapper.select:
select t1.*,t2.name as customer_name,t2.mobile_phone,t2.source,t2.source_name, t2.source_mobile,t2.channel,t3.name as match_user_name,t3.head,t4.short_name as project_name from busi_reception t1 inner join busi_customer t2 on t1.customer_id = t2.id inner join sys_user t3 on t1.saler_id=t3.user_id inner join busi_project t4 on t1.project_id=t4.id where exists(select 1 from busi_user_project t0 where t0.project_id=t1.project_id and t0.user_id=#{userId})
and t1.id = #{id}
and (t3.name like '%${matchUserName}%' or t3.first_letter like '%${matchUserName}')
and datediff(t1.create_time, #{createTime}) = 0
limit #{offset},#{limit}

select br.*,bc.name as customer_name from busi_reception br
left join busi_customer bc on br.customer_id = bc.id


        and br.saler_id = #{salerId}


        and br.status = #{status}


        and br.create_time > #{startDate}


        and br.create_time < #{endDate}


        and br.project_id = #{projectId}


order by br.create_time desc
limit #{offset},#{limit}

select count(*) from busi_reception br
left join busi_customer bc on br.customer_id = bc.id


        and br.saler_id = #{salerId}


        and br.status = #{status}


        and br.create_time > #{startDate}


        and br.create_time < #{endDate}


        and br.project_id = #{projectId}

SELECT DATE_FORMAT(temp.date,'%m/%d') as dateAbscissa, temp.date as fullDate, IFNULL(u.num,0) as num from( SELECT date_sub(DATE(#{endDate}),INTERVAL numlist.id DAY)AS 'date' FROM ( SELECT * from (SELECT i AS id FROM num ) a where a.id <=7 ) AS numlist
) temp LEFT JOIN ( SELECT DATE(c.create_time) AS udate, COUNT(1)as num FROM busi_reception c WHERE
c.project_id in
#{id}
GROUP BY DATE(c.create_time) ) u on DATE(temp.date) = u.udate ORDER BY temp.date desc

SELECT DATE_FORMAT(temp.date,'%Y\%m') as dateAbscissa, temp.date as fullDate, IFNULL(u.num,0) as num from( SELECT date_sub(DATE(#{endDate}),INTERVAL numlist.id MONTH)AS 'date' FROM ( SELECT * from (SELECT i AS id FROM num ) a where a.id <=7 ) AS numlist
) temp LEFT JOIN ( SELECT DATE_FORMAT(c.create_time,'%Y-%m') AS udate, COUNT(1)as num FROM busi_reception c WHERE
c.project_id in
#{id}
GROUP BY DATE_FORMAT(c.create_time,'%Y-%m') ) u on DATE_FORMAT(temp.date,'%Y-%m') = u.udate ORDER BY temp.date desc;

SELECT DATE_FORMAT(temp.date,'%Y') as dateAbscissa, temp.date as fullDate, IFNULL(u.num,0) as num from( SELECT date_sub(DATE(#{endDate}),INTERVAL numlist.id YEAR)AS 'date' FROM ( SELECT * from (SELECT i AS id FROM num ) a where a.id <=7 ) AS numlist
) temp LEFT JOIN ( SELECT DATE_FORMAT(c.create_time,'%Y') AS udate, COUNT(1)as num FROM busi_reception c WHERE
c.project_id in
#{id}
GROUP BY DATE_FORMAT(c.create_time,'%Y') ) u on DATE_FORMAT(temp.date,'%Y') = u.udate ORDER BY temp.date desc;
