mapper.select:
select distinct rm.menu_id from sys_user_role ur
	LEFT JOIN sys_role_menu rm on ur.role_id = rm.role_id
where ur.user_id = #{userId}

select t2.user_id,t2.username,t2.email,t2.mobile,t2.head,t2.name,t2.create_time,t3.id as is_signed from busi_user_project t1 left join sys_user t2 on t1.user_id = t2.user_id left join busi_sign_on t3 on t2.user_id=t3.user_id where 1=1
and t1.project_id =#{projectId}
and app_role = 2 and status=1 and t3.sign_on_date=#{today}
