mapper.select:
select distinct rm.menu_id from sys_user_role ur
	LEFT JOIN sys_role_menu rm on ur.role_id = rm.role_id
where ur.user_id = #{userId}

select DISTINCT t2.user_id,t2.username,t2.email,t2.mobile,t2.head,t2.name,t2.create_time,t3.id as is_signed from busi_user_project t1 left join sys_user t2 on t1.user_id = t2.user_id left join busi_sign_on t3 on t2.user_id=t3.user_id where 1=1
and t1.project_id in
#{id}
and app_role = 2 and status=1 and t3.sign_on_date=#{today}

select t1.* from sys_user t1 inner join busi_user_project t2 on t1.user_id = t2.user_id where t1.status=1
and t2.project_id =#{projectId}
and t1.middle_type_id =#{middleTypeId}
and t1.name like '%${name}%'

select t1.*
from sys_user t1
where coalesce(t1.middle_type_id,0)!=0
and exists(select 1 from busi_user_project t0
inner join busi_user_project t00 on t0.project_id=t00.project_id
where t0.user_id=t1.user_id and t00.user_id=#{userId})

    and t1.id =#{id}


    and t1.mobile like '%${mobile}%'


    and t1.middle_type_id =#{middleTypeId}


    and t1.middle_type_name like '%${middleTypeName}%'


        and t1.create_time >= '%${start}%'



        and t1.create_time <= '%${end}%'



    and t1.status =#{status}


    and t1.audit_status =#{auditStatus}

limit #{offset},#{limit}

select count(*) from sys_user t1 where coalesce(t1.middle_type_id,0)!=0 and exists(select 1 from busi_user_project t0 inner join busi_user_project t00 on t0.project_id=t00.project_id where t0.user_id=t1.user_id and t00.user_id=#{userId})
and t1.id =#{id}
and t1.mobile like '%${mobile}%'
and t1.middle_type_id =#{middleTypeId}
and t1.middle_type_name like '%${middleTypeName}%'
and t1.create_time >= '%${start}%'
and t1.create_time <= '%${end}%'
and t1.status =#{status}
and t1.audit_status =#{auditStatus}
